mapper.select:
SELECT uia.user_info_audit_id, case uia.user_info_audit_state when 1 then '未审核' when 2 then '拒绝' when 3 then '通过' end , from_unixtime(uia.user_info_audit_addtime,'%Y-%m-%d %H:%i:%S'),
oi.organization_name, oi.organization_code, u.user_mobile_phone
FROM user_info_audit AS uia LEFT JOIN organization_info oi ON ( uia.info_id = oi.organization_info_id ) where 1=1
and uia.user_info_audit_content =#{uia.user_info_audit_content}
and uia.user_info_audit_state =#{user_info_audit_state}
and uia.user_info_audit_type=#{user_info_audit_type}
and uia.user_info_audit_type=#{user_info_audit_type}
limt #{index},#{num}

SELECT uia.user_info_audit_id, uia.user_info_audit_state, uia.user_info_audit_addtime, uia.user_info_audit_type, oi.organization_name, oi.organization_code, uia.info_id FROM user_info_audit AS uia LEFT JOIN organization_info oi ON ( uia.info_id = oi.organization_info_id ) LEFT JOIN user u on( u.info_id=uia.info_id ) where uia.user_info_audit_content =1
and uia.user_info_audit_state =#{user_info_audit_state}
and uia.user_info_audit_type=#{user_info_audit_type}
and uia.user_info_audit_type=#{user_info_audit_type}

SELECT u.user_id, uia.user_info_audit_id, uia.user_info_audit_state, uia.user_info_audit_addtime, uia.user_info_audit_type, ei.expert_info_name, ei.expert_info_idcard, ei.expert_info_companyname, u.user_mobile_phone, uia.info_id FROM user_info_audit AS uia LEFT JOIN expert_info ei ON ( uia.info_id = ei.expert_info_id ) LEFT JOIN user u ON( u.info_id=ei.expert_info_id ) where uia.user_info_audit_content =2
and uia.user_info_audit_state =#{user_info_audit_state}
and uia.user_info_audit_type=#{user_info_audit_type}
and uia.user_info_audit_type=#{user_info_audit_type}

SELECT u.user_id, uia.user_info_audit_id, uia.user_info_audit_state, uia.user_info_audit_addtime, uia.user_info_audit_type, ei.expert_info_name, ei.expert_info_idcard, ei.expert_info_companyname, ei.expert_info_education, ei.expert_info_learnmajor, u.user_mobile_phone, uia.info_id FROM user_info_audit AS uia LEFT JOIN expert_info ei ON ( uia.info_id = ei.expert_info_id ) LEFT JOIN user u ON( u.info_id=ei.expert_info_id ) where uia.user_info_audit_content =2
and uia.user_info_audit_state =#{user_info_audit_state}
and uia.user_info_audit_type=#{user_info_audit_type}
and uia.user_info_audit_type=#{user_info_audit_type}
AND ( ei.expert_info_name like "%${condition}%" or ei.expert_info_idcard like "%${condition}%" or u.user_mobile_phone like "%${condition}%" )

SELECT u.user_id, uia.user_info_audit_id, uia.user_info_audit_state, uia.user_info_audit_addtime, uia.user_info_audit_type, oi.organization_name, oi.organization_code, u.user_mobile_phone, uia.info_id FROM user_info_audit AS uia LEFT JOIN organization_info oi ON ( uia.info_id = oi.organization_info_id ) LEFT JOIN user u ON( u.info_id= oi.organization_info_id ) where uia.user_info_audit_content =2
and uia.user_info_audit_state =#{user_info_audit_state}
and uia.user_info_audit_type=#{user_info_audit_type}
and uia.user_info_audit_type=#{user_info_audit_type}
AND ( oi.organization_name like "%${condition}%" or oi.organization_code like "%${condition}%" or u.user_mobile_phone like "%${condition}%" )

select count(*)
from user_info_audit
where user_id_add = #{Uid}
  AND user_info_audit_content = #{user_info_audit_content}
  And user_info_audit_state = 1
