mapper.insert:
INSERT INTO user ( user_mobile_phone, user_register_time, user_password, user_role
) VALUES (
#{user_mobile_phone}, #{user_register_time}, #{user_password}, #{user_role}
)

insert into organization_info_careerman ( organization_info_id, organization_careerman_name, organization_profession, organization_performance ) values (
#{organization_info_id}, #{organization_careerman_name}, #{organization_profession}, #{organization_performance},
)

mapper.select:
select

FROM user
where user_mobile_phone=#{user_mobile_phone} AND user_password=#{user_password}
